import React from "react";

class BookComponent extends React.Component {
  state = {
    title: ''
  }


  componentDidMount() {
    this.setState(
      {
        title: this.props.Title,
        authors: this.props.Authors,
        cover: this.props.Cover,
        shelf: this.props.Shelf
      }
    )
  }
  render() {
    return (
      <div className="book">
        <div className="book-top">
          <div className="book-cover" style={{ width: 128, height: 188, backgroundImage: `url(${this.state.cover})` }}></div>
          <div className="book-shelf-changer">
            <select name={this.props.Shelf} value={this.props.Shelf}>
              <option value="move">Move to...</option>
              <option value="currentlyReading">Currently Reading</option>
              <option value="wantToRead" defaultValue="selected">Want to Read</option>
              <option value="read">Read</option>
              <option value="none">None</option>
            </select>
          </div>
        </div>
        <div className="book-title">{this.state.title}</div>
        <div className="book-authors">{this.state.authors}</div>
      </div>
    )
  }
}

export default BookComponent
